def count(value, data):    # Initialize count to 0    count = 0    # Iterate over each element in data    for num in data:        # If element matches the value, increment count        if num == value:            count += 1    return countdef search(value, data):    # Iterate over each index and element pair in data    for i, num in enumerate(data):        # If element matches the value, return its index        if num == value:            return i    # If value not found, return -1    return -1def sort(data):    n = len(data)  # Get the length of the data list    for i in range(n):  # Iterate over each element of the list        for j in range(0, n-i-1):  # Iterate over each element except the last i elements            if data[j] > data[j+1]:  # If current element is greater than the next one                # Swap the elements to sort them in ascending order                data[j], data[j+1] = data[j+1], data[j]def delete(value, data):    i = 0    # Iterate over data    while i < len(data):        # If element matches value, remove it        if data[i] == value:            data.pop(i)        else:            i += 1# Ask the user to input some integersdata = []num_inputs = int(input("Enter the number of integers you want to input: "))# Gather user input for specified number of integersfor _ in range(num_inputs):    num = int(input("Enter an integer: "))    data.append(num)# Ask the user for the desired operationwhile True:    print("\nChoose one of the following options:")    print("1. Count")    print("2. Search")    print("3. Sort")    print("4. Delete")    print("5. Exit")    # Prompt user for choice    choice = int(input("Enter your choice (1-5): "))    if choice == 1:        # Get value from user and count occurrences        value = int(input("Enter the value to count: "))        print("Number of elements with value", value, ":", count(value, data))    elif choice == 2:        # Get value from user and search for it        value = int(input("Enter the value to search: "))        index = search(value, data)        # Display result of search        if index != -1:            print("Value", value, "found at index", index)        else:            print("Value", value, "not found in the list.")    elif choice == 3:        # Sort the list        sort(data)        print("Sorted list:", data)    elif choice == 4:        # Get value from user and delete all occurrences        value = int(input("Enter the value to delete: "))        delete(value, data)        print("List after deleting all occurrences of", value, ":", data)    elif choice == 5:        # Exit the program        print("Exiting program.")        break    else:        # Handle invalid choice        print("Invalid choice. Please enter a number between 1 and 5.")